        text-align: center;
        position: relative;
        z-index: 1;
    }
}

.toggle {
    position: absolute;
    border: 1px solid #B8B8B8;
    border-radius: 30px;
    background: var(--white);
    width: calc(50% - 10px);
    height: calc(100% - 10px);
    left: 6px;
    top: 5px;
    z-index: 0;
    transition: transform .3s;
}

.checkbox {
    display: none;
   
    &:checked + .toggle{
       transform: translateX(calc(100% + 9px));
    }
    &:checked + .names{
        span:nth-child(1){
            color: var(--blue);

        }
    }
}

.checkbox__label {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;  
    z-index: 2;
}

.cards {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 20px;
    align-items: center;
}

.card {
    padding: 38px 40px;
    border: 1px solid var(--border);
    border-radius: 10px;
    background: var(--white);
    position: relative;
    color: var(--blue);

    &:nth-child(2) {
        padding: 60px 40px;
        color: var(--white);
        background: var(--blue) url('../../assets/img/main-bg.png') no-repeat;
        background-size: cover;
        border-color: rgba(20, 86, 255, .5);
        background-position: right top;
    }

    .btn {
        max-width: 100%;
        padding: 15px;
    }
}

.card__title {
    font-size: 24px;
    font-weight: 700;
    margin-bottom: 51px;
    text-align: center;
}
.card__price {
    font-size: 24px;
